    }
  }); // TODO try/catch

  const isSame = await bcrypt.compare(password, user.password);

  if (user && isSame) {
    req.session.uid = user;
    console.log('AFTER LOGIN ', req.session.uid);
    res.redirect('/');
  } else {
    res.send({
      message: 'Не, такого челика нет, либ неверное пароль'
    }); // res.redirect('/login');
  }
});
module.exports = router;
