      title,
      description,
      img,
      user_id: req.session.uid.id
    });
    const home = React.createElement(Mk, {
      newCard
    });
    const html = ReactDOMServer.renderToStaticMarkup(home);
    res.write('<!DOCTYPE html>');
    res.end(html);
  }
});
